#!/bin/bash
./modelcount.sh -vars="methane,high_water,pump_on" "-ltl=((G((!methane | X(!pump_on))) & G((!high_water | X(pump_on)))) | F((high_water & pump_on & X((high_water & X(high_water))))))" "-ref=((G((!methane | X(!pump_on))) & G((!high_water | X(pump_on)))) | F((pump_on & X((high_water & X(high_water))))))" "-ref=(F((high_water & X(!pump_on))) | F((high_water & pump_on & X((high_water & F(high_water))))))" "-ref=((F((methane & X(pump_on))) | F((high_water & X(!pump_on)))) & G((!high_water | !pump_on | X((!high_water | X(pump_on))))))" "-ref=(G((!methane | X(!pump_on))) & G((!high_water | X(pump_on))))" "-ref=((G(X(!pump_on)) & G((!high_water | X(pump_on)))) | F((high_water & pump_on & X((high_water & X(high_water))))))" "-ref=(((!methane | X(!pump_on)) & G((!high_water | X(pump_on)))) | F((high_water & pump_on & X((high_water & X(high_water))))))" "-ref=((G((!methane | !pump_on)) & G((!high_water | X(pump_on)))) | F((high_water & pump_on & X((high_water & X(high_water))))))" "-ref=((G((!methane | X(!pump_on))) & G((!high_water | X(pump_on)))) | F((high_water & pump_on & X((high_water | X(high_water))))))" "-ref=((G((!methane | X(pump_on))) & G((!high_water | X(pump_on)))) | F((high_water & pump_on & X((high_water & X(high_water))))))" "-ref=((G((!methane | X(!pump_on))) & G((!high_water | X(pump_on)))) | (high_water & pump_on & X((high_water & X(high_water)))))" "-out=result/minepump/minepump-NO-precise.out" -k=6 -no-precise 